<?php/* * *@Shortcode Name : Categories *@retrun * */if ( ! function_exists( 'cs_pb_job_specialisms' ) ) {    function cs_pb_job_specialisms($die = 0){        global $cs_node, $post;                $shortcode_element = '';        $filter_element = 'filterdrag';        $shortcode_view = '';        $output = array();                $counter = $_POST['counter'];                $cs_counter = $_POST['counter'];        if ( isset($_POST['action']) && !isset($_POST['shortcode_element_id']) ) {            $POSTID = '';            $shortcode_element_id = '';        } else {            $POSTID = $_POST['POSTID'];            $PREFIX = 'cs_job_specialisms';            $parseObject     = new ShortcodeParse();            $shortcode_element_id = $_POST['shortcode_element_id'];            $shortcode_str = stripslashes ($shortcode_element_id);            $output = $parseObject->cs_shortcodes( $output, $shortcode_str , true , $PREFIX );        }        			$defaults = array(				'job_specialisms_title' => '',				'spec_cats' => '',				'cs_category_description'=>'',			);					            if(isset($output['0']['atts']))                $atts = $output['0']['atts'];            else                 $atts = array();            if(isset($output['0']['content']))                $job_specialisms_content = $output['0']['content'];            else                 $job_specialisms_content = '';            $job_specialisms_element_size = '25';            foreach($defaults as $key=>$values){                if(isset($atts[$key]))                    $$key = $atts[$key];                else                     $$key =$values;            }			            $name = 'cs_pb_job_specialisms';            $coloumn_class = 'column_'.$job_specialisms_element_size;                if(isset($_POST['shortcode_element']) && $_POST['shortcode_element'] == 'shortcode'){            $shortcode_element = 'shortcode_element_class';            $shortcode_view = 'cs-pbwp-shortcode';            $filter_element = 'ajax-drag';            $coloumn_class = '';        }  				$cs_rand_id = rand( 13441324, 93441324 );          ?><div id="<?php echo esc_attr($name.$cs_counter)?>_del" class="column  parentdelete <?php echo esc_attr($coloumn_class);?> <?php echo esc_attr($shortcode_view);?>" item="job_specialisms" data="<?php echo cs_element_size_data_array_index($job_specialisms_element_size)?>" >  <?php cs_element_setting($name,$cs_counter,$job_specialisms_element_size, '', 'ellipsis-h',$type='');?>  <div class="cs-wrapp-class-<?php echo intval($cs_counter)?> <?php echo esc_attr($shortcode_element);?>" id="<?php echo esc_attr($name.$cs_counter)?>" data-shortcode-template="[cs_job_specialisms {{attributes}}]{{content}}[/cs_job_specialisms]" style="display: none;">    <div class="cs-heading-area">      <h5><?php _e('Edit Categories Option','uoc');?></h5>      <a href="javascript:removeoverlay('<?php echo esc_js($name.$cs_counter)?>','<?php echo esc_js($filter_element);?>')" class="cs-btnclose"><i class="icon-times"></i></a> </div>    <div class="cs-pbwp-content">      <div class="cs-wrapp-clone cs-shortcode-wrapp">                <ul class="form-elements">          <li class="to-label">            <label><?php _e('Title','uoc');?></label>          </li>          <li class="to-field">            <input name="job_specialisms_title[]" type="text" value="<?php echo esc_attr($job_specialisms_title) ?>" />          </li>        </ul>                        <ul class="form-elements">					  <li class="to-label">						<label><?php _e('Description','uoc');?></label>					  </li>					  <li class="to-field">						<textarea name="cs_category_description[]" rows="8" cols="40" data-content-text="cs-shortcode-textarea">							<?php echo esc_textarea($cs_category_description);?>						</textarea>						<p><?php _e('Enter description here','uoc');?></p>					  </li>				</ul>                                        <?php				$spec_cats = explode(',', $spec_cats);				if( ! is_array( $spec_cats ) ) {			$spec_cats = array();		}		?>       		<ul class="form-elements">            <li class="to-label">            	<label><?php _e('Categories', 'uoc') ?></label>            </li>            <li class="to-field select-style">            	<select multiple="multiple" name="spec_cats[<?php echo absint( $cs_rand_id ) ?>][]" class="dropdown" style="height: 150px !important;">					<?php					$cs_all_cats = get_categories('taxonomy=course-category&child_of=0&hide_empty=0');					if( $cs_all_cats != '' ) {						foreach( $cs_all_cats as $cs_cat ){														?>							<option <?php if( in_array( $cs_cat->slug, $spec_cats ) ) echo 'selected="selected"' ?> value="<?php echo esc_attr( $cs_cat->slug ) ?>">							<?php echo cs_allow_special_char($cs_cat->name) ?>                            </option>							<?php						}					}                    ?>                </select>            </li>		</ul>                  </div>      <?php if(isset($_POST['shortcode_element']) && $_POST['shortcode_element'] == 'shortcode'){?>      <ul class="form-elements insert-bg">        <li class="to-field"> <a class="insert-btn cs-main-btn" onclick="javascript:Shortcode_tab_insert_editor('<?php echo str_replace('cs_pb_','',$name);?>','<?php echo esc_js($name.$cs_counter)?>','<?php echo esc_js($filter_element);?>')" ><?php _e('Insert','uoc');?></a> </li>      </ul>      <div id="results-shortocde"></div>      <?php } else {?>      <ul class="form-elements noborder">        <li class="to-label"></li>        <li class="to-field">          <input type="hidden" name="cs_orderby[]" value="job_specialisms" />          <input type="hidden" name="cs_spec_id[]" value="<?php echo absint( $cs_rand_id ) ?>" />          <input type="button" value="<?php _e('Save','uoc');?>" style="margin-right:10px;" onclick="javascript:_removerlay(jQuery(this))" />        </li>      </ul>      <?php } ?>    </div>  </div></div><?php        if ( $die <> 1 ) die();    }    add_action('wp_ajax_cs_pb_job_specialisms', 'cs_pb_job_specialisms');}