<?php/** * The template for displaying single post */	global $post, $cs_theme_options,$cs_single_view;	$cs_uniq = rand(11111111, 99999999);	$cs_postObject 			= get_post_meta($post->ID, 'cs_full_data', true);	$cs_gallery_ids 		= get_post_meta($post->ID, 'cs_post_list_gallery', true);	$cs_gallery_slider_ids 	= get_post_meta($post->ID, 'cs_post_detail_gallery', true);	$cs_gallery 			= explode(',', $cs_gallery_ids);	$cs_gallery_slider 		= explode(',', $cs_gallery_slider_ids);	get_header();	$cs_layout = '';	$leftSidebarFlag = false;	$rightSidebarFlag = false;		$cs_layout 					= get_post_meta($post->ID, 'cs_page_layout', true);	$cs_sidebar_left 			= get_post_meta($post->ID, 'cs_page_sidebar_left', true);	$cs_sidebar_right 			= get_post_meta($post->ID, 'cs_page_sidebar_right', true);	$post_tags_show 			= get_post_meta($post->ID, 'cs_post_tags_show', true);	$cs_post_social_sharing 	= get_post_meta($post->ID, 'cs_post_social_sharing', true);	$post_pagination_show 		= get_post_meta($post->ID, 'cs_post_pagination_show', true);	$cs_related_blog_post 		= get_post_meta($post->ID, 'cs_related_blog_post', true);	$inside_post_view 			= get_post_meta($post->ID, 'cs_detail_view', true);	$post_audio 				= get_post_meta($post->ID, 'cs_post_detail_audio', true);	$post_video 				= get_post_meta($post->ID, 'cs_post_detail_video', true);	$cs_single_view				= get_post_meta($post->ID, 'cs_single_view', true);		$cs_frame = '<i'.'frame ';		if ($cs_layout == "left") {		$cs_layout = "page-content";		$leftSidebarFlag = true;		$custom_height = 300;	} else if ($cs_layout == "right") {		$cs_layout = "page-content";		$rightSidebarFlag = true;		$custom_height = 300;	} else {		//$cs_layout = "page-content-fullwidth";		$cs_layout = "section-fullwidth";				$custom_height = 408;	}	if (!isset($cs_layout)) {		$cs_layout = isset($cs_theme_options['cs_single_post_layout']) ? $cs_theme_options['cs_single_post_layout'] : '';		if (isset($cs_layout) && $cs_layout == "sidebar_left") {			$cs_layout = "page-content";			$cs_sidebar_left = $cs_theme_options['cs_single_layout_sidebar'];			$leftSidebarFlag = true;			$custom_height = 300;		} else if (isset($cs_layout) && $cs_layout == "sidebar_right") {			$cs_layout = "page-content";			$cs_sidebar_right = $cs_theme_options['cs_single_layout_sidebar'];			$rightSidebarFlag = true;			$custom_height = 300;		}	}	$width  = 770;	$height = 433;	//770, 433			$image_url = cs_get_post_img_src($post->ID, $width, $height);	$cs_section_bg = $image_url <> '' ? esc_url( $image_url ) : '';		if(have_posts()) :		while (have_posts()) : the_post();	 	  ?>               		<div class="container"<?php if( $cs_single_view == 'view-2' ) { echo ' style="padding-top: 40px;"'; } ?>> 				<div class="row">					<?php 						if ($leftSidebarFlag == true) { ?>							<aside class="page-sidebar">								<?php 								if ( is_active_sidebar( cs_get_sidebar_id( $cs_sidebar_left ) ) ) {									if (!function_exists('dynamic_sidebar') || !dynamic_sidebar($cs_sidebar_left)) : endif; 								}								?>							</aside>						<?php						}					?>                      <div class="<?php echo esc_attr($cs_layout); ?>">                    						<?php							$cs_tags_name = 'post_tag';							$cs_categories_name = 'category';							$postname = 'post';                        ?>                                                    <div class="element-size-100">                                <div class="cs-blog-editor">                                                                                        <div class="cs-heading-sec">                                  <div class="inner-sec">                                    <h1><?php the_title(); ?></h1>                                  </div>                                </div>                                                                   <?php                                    if (isset($inside_post_view) and $inside_post_view <> '') {                                        $viewType = '<i class="icon-camera"></i>';                                        if ($inside_post_view == "slider") {                                            $viewType = '<i class="icon-unsorted"></i>';                                            echo '<div class="cs-main-post"><figure class="">';                                            cs_post_flex_slider($width, $height, get_the_id(), 'post');                                            echo '</figure></div>';										} else if ($inside_post_view == "single" && $image_url <> '') {                                                                                        echo '<div class="cs-main-post">                                                    <figure >';                                                        echo '<img src="' . esc_url($image_url) . '" alt="'.cs_get_post_img_title($post->ID).'" >';                                                    echo '</figure>                                                </div>';                                        } else if ($inside_post_view == "video" and $post_video <> '' and $inside_post_view <> '') {                                            $viewType = '<i class="icon-film"></i>';                                            $url = parse_url($post_video);                                            if ($url['host'] == $_SERVER["SERVER_NAME"]) {                                                echo '<div class="cs-main-post"><figure class="">';                                                echo do_shortcode('[video width="' . $width . '" height="' . $height . '" src="' . esc_url($post_video) . '"][/video]');                                                echo '</figure></div>';                                            }else {                                                												if ($url['host'] == 'vimeo.com') {                                                    echo '<div class="cs-main-post"><figure class="">';                                                    $content_exp = explode("/", $post_video);                                                    $content_vimo = array_pop($content_exp);                                                    echo '<figure>'.$cs_frame.' width="' . $width . '" height="' . $height . '" src="https://player.vimeo.com/video/' . $content_vimo . '"                                                     frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>';                                                    echo '</figure></div>';                                                } elseif ($url['host'] == 'soundcloud.com') {                                        $video	= wp_oembed_get($post_video,array('height' => $custom_height));                                        $search = array('webkitallowfullscreen', 'mozallowfullscreen', 'frameborder="0"');                                        echo '<div class="cs-main-post"><figure>';                                        echo  str_replace($search,'',$video);                                        echo '</figure>';                                        echo '</div>';                                                  }else {                                                    echo '<div class="cs-media"><figure class="detailpost cs-detail-post">';                                                    $content = str_replace(array('watch?v=', 'http://www.dailymotion.com/'), array('embed/', '//www.dailymotion.com/embed/'), $post_video);                                                    echo cs_allow_special_char($cs_frame).' width="' . $width . '" height="' . $height . '" src="' .esc_url($content) . '" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>';                                                    echo '</figure></div>';                                                }                                            }                                        } elseif ($inside_post_view == "audio" and $inside_post_view <> '') {                                            $viewType = '<i class="icon-microphone"></i>';                                            echo '<div class="cs-media"><figure class="detailpost">';                                            echo do_shortcode('[audio mp3="' . $post_audio . '"][/audio]');                                            echo '</figure></div>';                                        }                                     }									else if ($inside_post_view == "" && $image_url <> '') {										echo '<div class="cs-main-post">												<figure >';													echo '<img src="' . esc_url($image_url) . '" alt="" >';												echo '</figure>											</div>';									}									?>                                  <div class="date-time">                                    <?php                                         $getMonth =  date('M', strtotime(get_the_date()));                                        $getDay =  date('d', strtotime(get_the_date()));                                    ?>                                                                  <time datetime="2011-01-12"><span><?php echo esc_attr($getMonth) ?></span><small><?php echo esc_attr($getDay) ?></small></time>                                </div>                                <div class="cs-post-panel">                                                                 <div class="cs-thumb-post">                                      <figure><a href="<?php echo esc_url(get_author_posts_url(get_the_author_meta('ID'))); ?>">                                                <?php echo get_avatar(get_the_author_meta('ID'), 40); ?></a></figure>                                      <ul>                                        <li><span> <?php _e('Posted by', 'uoc') ?></span>                                        <i class="icon-user9"></i>                                            <a href="<?php echo esc_url(get_author_posts_url(get_the_author_meta('ID'))); ?>">                                                <?php echo get_the_author(); ?>                                             </a>                                        </li>                                                                                <?php                                        $categories_list = get_the_term_list(get_the_id(), 'category', '', ', ', '');                                        if ($categories_list) {                                        ?>                                            <li><span><?php _e('Posted in', 'uoc') ?></span>                                            <i class="icon-folder5"></i>                                                <?php printf('%1$s', $categories_list); ?>                                            </li>                                        <?php                                        } ?>                                                                                </ul>                                    </div>                                    <div style="clear:both;"></div>                                    <div class="cs-editor-text">                                      <?php the_content(); ?>                                    </div>                                                                          <div class="cs-social-share">                                      <?php 									   if ($cs_post_social_sharing == "on") {										     $post_social_sharing_text = __('Share Post', 'uoc');                                       cs_social_share_blog(false, true, $post_social_sharing_text);                                       }							                                     ?>                                                                          <?php                                       if ($post_tags_show) {                                        $cs_tags_list = get_the_term_list(get_the_id(), 'post_tag', '', ', ', '');                                        if ($cs_tags_list) {                                        ?>                                             <div class="cs-tags">                                             <span><?php _e('Post Tags', 'uoc') ?></span>                                                <ul>                                                    <li><a><i class="icon-tag7"></i></a>                                                    <?php printf('%1$s', $cs_tags_list); ?>                                                    </li>                                                </ul>                                             </div>                                        <?php                                        }                                     }                                    ?>                                   </div>                                                                        <?php                                    $thumb_ID = get_post_thumbnail_id($post->ID);                                    if ($images = get_children(array(                                        'post_parent' => get_the_ID(),                                        'post_type' => 'attachment',                                        'exclude' => $thumb_ID,                                    ))) {                                        ?>                                    <div class="cs-attachment">                                    <h6> <?php _e('Blog Attachment','uoc')?> </h6>                                    <ul>                                                                        <?php foreach ($images as $image) {  ?>                                       <?php                                                   if ($image->post_mime_type == 'image/png' || $image->post_mime_type == 'image/gif' || $image->post_mime_type == 'image/jpg' || $image->post_mime_type == 'image/jpeg'){   			$image_url = cs_attachment_image_src((int) $image->ID, 150, 150);?>                                                                                                    <li>                                                        <a href="<?php echo esc_url($image->guid); ?>"><figure><i class=" icon-camera11"></i></figure></a>                                                    </li>                                                    <?php }else if ($image->post_mime_type == 'application/zip') {  ?>                                                                                                        <li>                                                        <a href="<?php echo esc_url($image->guid); ?>"><figure><i class="icon-video-camera"></i></figure></a>                                                    </li>                                                                                                        <?php  } else if ($image->post_mime_type == 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') { ?>                                                                                                        <li>                                                     <a href="<?php echo esc_url($image->guid); ?>"><figure><i class="icon-file-text-o"></i></figure></a>                                                    </li>                                                                                                        <?php  	} else if ($image->post_mime_type == 'text/plain') { ?>                                                                                                        <li>                                                        <a href="<?php echo esc_url($image->guid); ?>"><figure><i class="icon-file-text-o"></i></figure></a>                                                    </li>	                                                    <?php  	}else if ($image->post_mime_type == 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {  ?>                                                                                                        <li>                                                        <a href="<?php echo esc_url($image->guid); ?>"><figure><i class="icon-file-text-o"></i></figure></a>                                                    </li>                                                                                                <?php  }                                        // loop end                                     }                                   ?>                                    </ul>                                  </div>                                <?php } ?>                                                                  </div>                                 <?php                                 if (isset($post_pagination_show) && $post_pagination_show == 'on') {                                    cs_next_prev_custom_links('post');                                }                                ?>                                                                <?php                                  if (isset($cs_related_blog_post) && $cs_related_blog_post == 'on') {                                    cs_related_post('','3');                                }                                ?>                                                                <?php                                  if('open' == $post->comment_status){                                    comments_template('', true);                                }                                ?>                                 </div>                                </div>                                                   </div>                    <?php					if ($rightSidebarFlag == true) {					?>					<aside class="page-sidebar">						<?php 						if ( is_active_sidebar( cs_get_sidebar_id( $cs_sidebar_right ) ) ) {							if (!function_exists('dynamic_sidebar') || !dynamic_sidebar($cs_sidebar_right)) : endif; 						}						?>					</aside>					<?php					}					?>                			</div>            </div>           			<?php		endwhile;	endif;	get_footer(); ?>           