INSERT INTO `m_articles` (`a_id`, `a_category_id`, `a_type_id`, `a_title`, `a_short_desc`, `a_description`, `a_images`, `a_dspl_flag`, `a_import_flag`, `a_free1`, `a_free2`, `a_free3`, `a_free4`, `a_free5`, `last_date`, `last_kind`, `last_ipadrs`, `last_user`) VALUES
(154, 3, 2, 'Danh sách mới nhất về ngành nghề được phép định cư Úc 2017', 'Từ ngày 19/4/2017, Danh sách Kỹ năng Chiến lược Trung hạn và Dài hạn (Medium and Long-term Strategic Skills List - MLTSSL) sẽ thay thế Danh sách Nghề nghiệp có Kỹ năng (Skilled Occupation List - SOL) được định cư Úc 2017.', '<div class="newscontent">\r\n                            <p style="text-align: justify; ">Được học tập, làm việc và sinh sống tại đất nước Úc hiện đại &#160;là mơ ước của bao du học sinh. Do đó, định cư Úc luôn là chủ đề được nhiều phụ huynh và du học sinh quan tâm theo dõi.&#160;</p>\r\n<p style="text-align: justify; "><img data-src="sydney.png" width="600" height="400" alt=""></p>\r\n<p style="text-align: justify; ">Theo thông tin mới nhất, Bộ di trú Úc vừa công bố danh sách ngành nghề trung và dài hạn được phép định cư Úc 2017. Theo đó, bạn phải chỉ định một nghề nghiệp trong Danh sách Kỹ năng Chiến lược Trung hạn và dài hạn (MLTSSL) nếu bạn đang nộp đơn xin bất kỳ diện visa dưới đây:</p>\r\n<p style="text-align: justify; ">•<span class="Apple-tab-span" style="white-space:pre">	</span>Visa tay nghề độc lập (visa 189)</p>\r\n<p style="text-align: justify; ">•<span class="Apple-tab-span" style="white-space:pre">	</span>Visa tạm thời tay nghề khu vực vùng miền (visa 489) (trừ khi được Chính phủ Tiểu bang hoặc Lãnh thổ đề cử)</p>\r\n<p style="text-align: justify; ">•<span class="Apple-tab-span" style="white-space:pre">	</span>Visa dành cho sinh viên mới tốt nghiệp (visa 485) – Dòng tốt nghiệp (Graduate Work Stream).</p>\r\n<p style="text-align: justify; ">&#160;<img data-src="135919053.png" width="600" height="432" alt=""></p>\r\n<p style="text-align: justify; "><strong>Lưu ý:</strong> 16 ngành nghề khác của MLTSSL bị hạn chế chỉ áp dụng cho các chương trình thị thực</p>\r\n<p style="text-align: justify; ">•<span class="Apple-tab-span" style="white-space:pre">	</span>Có kỹ năng độc lập (visa 189)</p>\r\n<p style="text-align: justify; ">•<span class="Apple-tab-span" style="white-space:pre">	</span>Du học sinh mới tốt nghiệp (visa 485)</p>\r\n<p style="text-align: justify; ">•<span class="Apple-tab-span" style="white-space:pre">	</span>Có kỹ năng khu vực (tạm thời) (visa 489), nếu người nộp đơn không được cơ quan chính phủ Tiểu Bang hoặc Lãnh Thổ đề cử</p>\r\n<p style="text-align: justify; "><img data-src="Work-Visa.png" width="600" height="429" alt=""></p>\r\n<p style="text-align: justify; ">Nội dung chi tiết của Danh sách MLTSSL được cập nhật ngay dưới đây:</p>\r\n<table style="width: 98.3444%;">\r\n    <thead>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p><strong>OCCUPATION133111</strong></p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p><strong>ANZSCO CODE</strong></p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p><strong>ASSESSING AUTHORITY</strong></p>\r\n            </td>\r\n        </tr>\r\n    </thead>\r\n    <tbody>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p>Construction Project Manager</p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p>133111</p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p>VETASSESS</p>\r\n            </td>\r\n        </tr>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p>Project Builder</p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p>133112</p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p>VETASSESS</p>\r\n            </td>\r\n        </tr>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p>Engineering Manager</p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p>133211</p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p>Engineers Australia/AIM</p>\r\n            </td>\r\n        </tr>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p>Production Manager (Mining)</p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p>133513</p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p>VETASSESS</p>\r\n            </td>\r\n        </tr>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p>Child Care Centre Manager</p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p>134111</p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p>TRA</p>\r\n            </td>\r\n        </tr>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p>Medical Administrator</p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p>134211</p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p>VETASSESS</p>\r\n            </td>\r\n        </tr>\r\n        <tr style="height: 35px;">\r\n            <td style="width: 47%; height: 35px;">\r\n            <p>Nursing Clinical Director</p>\r\n            </td>\r\n            <td style="width: 17.3514%; height: 35px; text-align: center;">\r\n            <p>134212</p>\r\n            </td>\r\n            <td style="width: 43.6486%; height: 35px;">\r\n            <p>ANMAC</p>\r\n            </td>\r\n        </tr>\r\n        <tr style=